import React, { useState } from "react";
import { Select, Typography, Row, Col, Avatar, Card } from "antd";
import moment from "moment";
import { useGetCryptoNewsQuery } from "../services/cryptoNews";
import { useGetCryptosQuery } from "../services/cryptoApi";

const { Text, Title } = Typography;
const { Option } = Select;

const demoImg = "https://www.bing.com/th?id=OVFT.mpzuVZnv8dwIMRfQGPbOPC&pid=News";

const News = ({ simplified }) => {
    const [newsCategory, setNewsCategory] = useState(`Cryptocurrency`);
    const { data: cryptoNews } = useGetCryptoNewsQuery({ newsCategory: newsCategory, count: 
        simplified ? 6 : 12});
    const { data } = useGetCryptosQuery(100);

    if(!cryptoNews?.value) return "Loading...";
    return (
       <Row gutter={[24, 24]}>
           {!simplified && (
               <Col span={24}>
                   <Select
                    showSearch
                    className="select-news"
                    palceholder="Select a Crypto"
                    optionFilterProp="children"
                    onChange={(value) => console.log(value)}
                    filterOption={(input, option) => option.children.toLowerCae().indexOf(input.toLowerCase()) >= 0}>
                        <Option value="Cryptocurtrency">Cryptocurrency</Option>
                   </Select>
               </Col>
           )}
           {cryptoNews.value.map((news, i) => (
               <Col xs={24} sm={12} lg={8} key={i}>
                   <Card hoverable className="news-card">
                       <a href={news.url} target="_blank" rel="noreferrer">
                           <div className="news-large-container">
                               <Title className="news-title" level={4}>{news.name}</Title>
                               <img src={news?.image?.thumbnail?.contentUrl || demoImg} alt="news" />
                           </div>
                           <p>
                               {news.description > 100 ? `${news.description.substring(0, 100)}...` : `${news.description}`}
                           </p>
                           <div className="provider-container">
                               <div>
                                   <Avatar src={news.provider[0]?.image?.thumbnail?.contentUrl || demoImg} />
                                   <Text className="provider-name">{news.provider[0]?.name}</Text>
                               </div>
                                   <Text>{moment(news.datePublished).startOf(`ss`).fromNow()}</Text>
                           </div>
                       </a>
                   </Card>
               </Col>
           ))}
       </Row>
    );
}

export default News;
